import React, { Component } from "react"
import axios from "axios"

class Pizzas extends Component {
  static async getInitialProps({ query }) {
    let pizzaData

    try {
      const response = await axios.get(`http://localhost:3000/api/v1/pizza/${query.pizzaName}`)
      pizzaData = response.data
    } catch (error) {
      console.error(error)
    }

    return { pizzaData }
  }
  render() {
    console.log(this.props)
    let pizza = this.props.pizzaData[0]
    return (
      <div>
        {pizza ? (
          <div>
            <h2>{pizza ? pizza.name : null}</h2>
            <div style={{ background: `url("/static/images/${pizza.image}")` }}></div>
            <img src={`url("/static/images/${pizza.image}")`} alt="" />
          </div>
        ) : null}
      </div>
    )
  }
}

export default Pizzas
