import React from "react"
import Link from "next/link"

const Pizzas = (props) => {
  console.log(props)
  const showList = () => {
    props.pizzas.map((pizza) => {
      if (pizza.pod !== true) {
        return (
          <Link href={{ pathname: "/pizzas", query: { pizzaName: pizza.idName } }} as={`/pizzas/${pizza.idName}`}>
            <a>
              <div className="item">
                <div className="inner-item" style={{ background: `url("/static/images/pizza_2.jpg")` }}>
                  <div className="overlay"></div>
                  <div className="item-nfo">
                    <h3>American</h3>
                    <p>
                      Lorem ipsum dolor sit amet consectetur adipisicing elit. Laboriosam corrupti aperiam nesciunt.
                      Hic, odio necessitatibus.
                    </p>
                  </div>
                </div>
              </div>
            </a>
          </Link>
        )
      }
    })
  }
  return (
    <div className="pizzas_wrapper">
      <div className="articles">{showList()}</div>
    </div>
  )
}

export default Pizzas
