import React, { Component } from "react"
import Featured from "../components/includes/home/featured"
import Pizzas from "../components/includes/home/pizzas"
import axios from "axios"

class Home extends Component {
  static async getInitialProps() {
    let pizzasData

    try {
      const response = await axios.get("http://localhost:3000/api/v1/pizza")
      pizzasData = response.data
    } catch (error) {
      console.error(error)
    }

    return {
      pizzasData,
    }
  }
  render() {
    console.log(this.props)
    return (
      <div>
        <Featured />
        <Pizzas />
      </div>
    )
  }
}

Home.getInitialProps = async ({ pathname, query }) => {
  let pizzasData
  try {
    const response = await axios.get("http://localhost:3000/api/v1/pizza")
    pizzasData = response.data
  } catch (error) {
    console.error(error)
  }
  return { custom: "custom" } // pass some custom props to component
}

export default Home
