import axios from "axios"
import { SEND_MESSAGE, CLEAR_MESSAGE } from "../reducers/types"
import { response } from "express"

const URL = "http://localhost:3000"

export const sendMessage = (data) => {
  //because of redux-romise we dont need async await
  const request = axios({
    method: "POST",
    url: `${url}/api/v1/messages`,
    data: JSON.stringify(data),
    headers: { "Content-Type": "application/json" },
  }).then((response) => response.data)

  return { type: SEND_MESSAGE, payload: request }
}
export const clearMessage = (data) => {
  return { type: CLEAR_MESSAGE, payload: "" }
}
